 30	
 31	public class JasperReportsUtil {
 32	 
 33	 private static final String REPORT_TEMPLATE = "/reports/customer_report.jrxml";
 34	 private static JasperReport compiledReport;
 35	 
 36	 /**
 37	 * Compile the JasperReports template
 38	 */
 39	 private static void compileReport() throws JRException {
 40	 if (compiledReport == null) {
 41	 InputStream reportStream = JasperReportsUtil.class.getResourceAsStream(REPORT_TEMPLATE);
 42	 if (reportStream == null) {
 43	 throw new JRException("Report template not found: " + REPORT_TEMPLATE);
 44	 }
 45	 compiledReport = JasperCompileManager.compileReport(reportStream);
 46	 }
 47	 }
 48	 
 49	 /**
 50	 * Generate JasperPrint object from customer data
 51	 */
 52	 public static JasperPrint generateReport(List<Customer> customers) throws JRException {
 53	 compileReport();
 54	 
 55	 // Create data source
 56	 JRBeanCollectionDataSource dataSource = new JRBeanCollectionDataSource(customers);
 57	 
 58	 // Create parameters map
 59	 Map<String, Object> parameters = new HashMap<>();
 60	 parameters.put("ReportTitle", "Customer Management Report");
 61	 parameters.put("GeneratedBy", "Customer Management System");
 62	 
 63	 // Fill the report
 64	 return JasperFillManager.fillReport(compiledReport, parameters, dataSource);
 65	 }
