 115	 /**
 116	 * Export report to PDF file
 117	 */
 118	 public static void exportToPDF(List<Customer> customers, Stage parentStage) {
 119	 try {
 120	 JasperPrint jasperPrint = generateReport(customers);
 121	 
 122	 // Show file chooser
 123	 FileChooser fileChooser = new FileChooser();
 124	 fileChooser.setTitle("Save PDF Report");
 125	 fileChooser.setInitialFileName("customer_report.pdf");
 126	 fileChooser.getExtensionFilters().add(
 127	 new FileChooser.ExtensionFilter("PDF Files", "*.pdf")
 128	 );
 129	 
 130	 File file = fileChooser.showSaveDialog(parentStage);
 131	 if (file != null) {
 132	 // Export to PDF
 133	 JRPdfExporter exporter = new JRPdfExporter();
 134	 exporter.setExporterInput(new SimpleExporterInput(jasperPrint));
 135	 exporter.setExporterOutput(new SimpleOutputStreamExporterOutput(new FileOutputStream(file)));
 136	 
 137	 SimplePdfExporterConfiguration configuration = new SimplePdfExporterConfiguration();
 138	 exporter.setConfiguration(configuration);
 139	 
 140	 exporter.exportReport();
 141	 
 142	 // Show success message
 143	 showInfoAlert("Export Success", "PDF report has been saved successfully!", 
 144	 "File saved to: " + file.getAbsolutePath());
 145	 }
 146	 
 147	 } catch (Exception e) {
 148	 showErrorAlert("Export Error", "Failed to export PDF", e.getMessage());
 149	 }
 150	 }
