 571	 private void showCustomerDetailDialog(Customer customer) {
 572	 Dialog<ButtonType> dialog = new Dialog<>();
 573	 dialog.setTitle("Customer Details");
 574	 dialog.setHeaderText("Customer: " + customer.getName());
 575	 
 576	 // Set the button types
 577	 ButtonType editButtonType = new ButtonType("Edit", ButtonBar.ButtonData.LEFT);
 578	 ButtonType deleteButtonType = new ButtonType("Delete", ButtonBar.ButtonData.LEFT);
 579	 dialog.getDialogPane().getButtonTypes().addAll(editButtonType, deleteButtonType, ButtonType.CLOSE);
 580	 
 581	 // Create the details grid
 582	 GridPane grid = new GridPane();
 583	 grid.setHgap(10);
 584	 grid.setVgap(10);
 585	 grid.setPadding(new Insets(20, 150, 10, 10));
 586	 
 587	 grid.add(new Label("ID:"), 0, 0);
 588	 grid.add(new Label(String.valueOf(customer.getIdx())), 1, 0);
 589	 grid.add(new Label("NIK:"), 0, 1);
 590	 grid.add(new Label(customer.getNik()), 1, 1);
 591	 grid.add(new Label("Name:"), 0, 2);
 592	 grid.add(new Label(customer.getName()), 1, 2);
 593	 grid.add(new Label("Born Date:"), 0, 3);
 594	 grid.add(new Label(customer.getBorn() != null ? customer.getBorn().toString() : ""), 1, 3);
 595	 grid.add(new Label("Active:"), 0, 4);
 596	 grid.add(new Label(customer.isActive() ? "Yes" : "No"), 1, 4);
 597	 grid.add(new Label("Salary:"), 0, 5);
 598	 grid.add(new Label(String.format("%,d", customer.getSalary())), 1, 5);
 599	 
 600	 dialog.getDialogPane().setContent(grid);
 601	 
 602	 Optional<ButtonType> result = dialog.showAndWait();
 603	 
 604	 if (result.isPresent()) {
 605	 if (result.get() == editButtonType) {
 606	 showEditCustomerDialog(customer);
 607	 } else if (result.get() == deleteButtonType) {
 608	 showDeleteConfirmation(customer);
 609	 }
 610	 }
 611	 }
