 618	 // Set the button types
 619	 ButtonType saveButtonType = new ButtonType("Save", ButtonBar.ButtonData.OK_DONE);
 620	 dialog.getDialogPane().getButtonTypes().addAll(saveButtonType, ButtonType.CANCEL);
 621	 
 622	 // Create the form grid
 623	 GridPane grid = new GridPane();
 624	 grid.setHgap(10);
 625	 grid.setVgap(10);
 626	 grid.setPadding(new Insets(20, 150, 10, 10));
 627	 
 628	 TextField nikField = new TextField(customer.getNik());
 629	 TextField nameField = new TextField(customer.getName());
 630	 DatePicker bornDatePicker = new DatePicker(customer.getBorn());
 631	 CheckBox activeCheckBox = new CheckBox();
 632	 activeCheckBox.setSelected(customer.isActive());
 633	 TextField salaryField = new TextField(String.valueOf(customer.getSalary()));
 634	 
 635	 grid.add(new Label("NIK:"), 0, 0);
 636	 grid.add(nikField, 1, 0);
 637	 grid.add(new Label("Name:"), 0, 1);
 638	 grid.add(nameField, 1, 1);
 639	 grid.add(new Label("Born Date:"), 0, 2);
 640	 grid.add(bornDatePicker, 1, 2);
 641	 grid.add(new Label("Active:"), 0, 3);
 642	 grid.add(activeCheckBox, 1, 3);
 643	 grid.add(new Label("Salary:"), 0, 4);
 644	 grid.add(salaryField, 1, 4);
 645	 
 646	 dialog.getDialogPane().setContent(grid);
 647	 
 648	 // Request focus on the name field by default
 649	 Platform.runLater(nameField::requestFocus);
 650	 
 651	 // Convert the result to a customer when the save button is clicked
 652	 dialog.setResultConverter(dialogButton -> {
 653	 if (dialogButton == saveButtonType) {
 654	 try {
 655	 Customer updatedCustomer = new Customer();
 656	 updatedCustomer.setIdx(customer.getIdx());
 657	 updatedCustomer.setNik(nikField.getText());
 658	 updatedCustomer.setName(nameField.getText());
 659	 updatedCustomer.setBorn(bornDatePicker.getValue());
 660	 updatedCustomer.setActive(activeCheckBox.isSelected());
 661	 updatedCustomer.setSalary(Integer.parseInt(salaryField.getText()));
 662	 return updatedCustomer;
 663	 } catch (NumberFormatException e) {
 664	 showAlert(Alert.AlertType.ERROR, "Input Error", "Invalid salary format", "Please enter a valid number for salary.");
 665	 return null;
 666	 }
 667	 }
 668	 return null;
 669	 });
 670	 
 671	 Optional<Customer> result = dialog.showAndWait();
 672	 
 673	 result.ifPresent(updatedCustomer -> {
 674	 Alert confirmAlert = new Alert(Alert.AlertType.CONFIRMATION);
 675	 confirmAlert.setTitle("Confirm Update");
 676	 confirmAlert.setHeaderText("Update Customer");
 677	 confirmAlert.setContentText("Are you sure you want to update this customer?");
 678	 
 679	 Optional<ButtonType> confirmResult = confirmAlert.showAndWait();
 680	 if (confirmResult.isPresent() && confirmResult.get() == ButtonType.OK) {
 681	 try {
 682	 controller.updateCustomer(updatedCustomer);
 683	 showAlert(Alert.AlertType.INFORMATION, "Success", "Customer Updated", "Customer has been successfully updated.");
 684	 loadData();
 685	 } catch (SQLException e) {
 686	 showAlert(Alert.AlertType.ERROR, "Database Error", "Failed to update customer", e.getMessage());
 687	 }
 688	 }
 689	 });
 690	 }
 691	 
 692	 private void showDeleteConfirmation(Customer customer) {
 693	 Alert alert = new Alert(Alert.AlertType.CONFIRMATION);
 694	 alert.setTitle("Confirm Delete");
 695	 alert.setHeaderText("Delete Customer");
 696	 alert.setContentText("Are you sure you want to delete customer: " + customer.getName() + "?");
 697	 
 698	 Optional<ButtonType> result = alert.showAndWait();
 699	 if (result.isPresent() && result.get() == ButtonType.OK) {
 700	 try {
 701	 controller.deleteCustomer(customer.getIdx());
 702	 showAlert(Alert.AlertType.INFORMATION, "Success", "Customer Deleted", "Customer has been successfully deleted.");
 703	 loadData();
 704	 } catch (SQLException e) {
 705	 showAlert(Alert.AlertType.ERROR, "Database Error", "Failed to delete customer", e.getMessage());
 706	 }
 707	 }
 708	 }
 709	 
 710	 private void showNotConnectedAlert() {
 711	 showAlert(Alert.AlertType.WARNING, "Not Connected", "Database Connection Required", 
 712	 "Please connect to the database first using Connect > Open Connection menu.");
 713	 }
