 461	 private void loadData() {
 462	 if (!DatabaseConnection.isConnected()) {
 463	 System.out.println("DEBUG: Database not connected");
 464	 return;
 465	 }
 466	
 467	 try {
 468	 int offset = (currentPage - 1) * rowsPerPage;
 469	 System.out.println("DEBUG: Loading data - Page: " + currentPage + ", RowsPerPage: " + rowsPerPage + ", Offset: " + offset + ", Search: '" + currentSearch + "'");
 470	
 471	 List<Customer> customers = controller.getCustomers(rowsPerPage, offset, currentSearch);
 472	 totalRecords = controller.getTotalCustomers(currentSearch);
 473	 totalPages = totalRecords > 0 ? (int) Math.ceil((double) totalRecords / rowsPerPage) : 1;
 474	
 475	 System.out.println("DEBUG: Retrieved " + customers.size() + " customers, Total records: " + totalRecords + ", Total pages: " + totalPages);
 476	
 477	 ObservableList<Customer> data = FXCollections.observableArrayList(customers);
 478	 tableView.setItems(data);
 479	
 480	 updatePaginationControls();
 481	 } catch (SQLException e) {
 482	 System.err.println("DEBUG: SQL Error: " + e.getMessage());
 483	 e.printStackTrace();
 484	 showAlert(Alert.AlertType.ERROR, "Database Error", "Failed to load customer data", e.getMessage());
 485	 }
 486	 }
 487	 
 488	 private void updatePaginationControls() {
 489	 String pageText = String.format("Page %d of %d (Total records: %d)", currentPage, totalPages, totalRecords);
 490	 pageInfoLabel.setText(pageText);
 491	
 492	 boolean prevDisabled = currentPage <= 1;
 493	 boolean nextDisabled = currentPage >= totalPages || totalPages == 0;
 494	
 495	 prevButton.setDisable(prevDisabled);
 496	 nextButton.setDisable(nextDisabled);
 497	
 498	 System.out.println("DEBUG: Pagination updated - " + pageText);
 499	 System.out.println("DEBUG: Previous button disabled: " + prevDisabled + ", Next button disabled: " + nextDisabled);
 500	 }
