 13	 public List<Customer> getAllCustomers(int limit, int offset, String searchTerm) throws SQLException {
 14	 List<Customer> customers = new ArrayList<>();
 15	 String query = "SELECT * FROM customer WHERE 1=1";
 16	
 17	 if (searchTerm != null && !searchTerm.isEmpty()) {
 18	 query += " AND (nik LIKE ? OR name LIKE ? OR born LIKE ? OR CAST(active AS CHAR) LIKE ? OR CAST(salary AS CHAR) LIKE ?)";
 19	 }
 20	
 21	 query += " LIMIT ? OFFSET ?";
 22	
 23	 System.out.println("DEBUG: Executing query: " + query);
 24	 System.out.println("DEBUG: Parameters - limit: " + limit + ", offset: " + offset + ", searchTerm: '" + searchTerm + "'");
 25	
 26	 Connection conn = DatabaseConnection.getConnection();
 27	 try (PreparedStatement stmt = conn.prepareStatement(query)) {
 28	
 29	 int paramIndex = 1;
 30	 if (searchTerm != null && !searchTerm.isEmpty()) {
 31	 String searchPattern = "%" + searchTerm + "%";
 32	 stmt.setString(paramIndex++, searchPattern);
 33	 stmt.setString(paramIndex++, searchPattern);
 34	 stmt.setString(paramIndex++, searchPattern);
 35	 stmt.setString(paramIndex++, searchPattern);
 36	 stmt.setString(paramIndex++, searchPattern);
 37	 }
 38	
 39	 stmt.setInt(paramIndex++, limit);
 40	 stmt.setInt(paramIndex, offset);
 41	
 42	 try (ResultSet rs = stmt.executeQuery()) {
 43	 while (rs.next()) {
 44	 Customer customer = new Customer();
 45	 customer.setIdx(rs.getInt("idx"));
 46	 customer.setNik(rs.getString("nik"));
 47	 customer.setName(rs.getString("name"));
 48	
 49	 Date bornDate = rs.getDate("born");
 50	 if (bornDate != null) {
 51	 customer.setBorn(bornDate.toLocalDate());
 52	 }
 53	
 54	 customer.setActive(rs.getBoolean("active"));
 55	 customer.setSalary(rs.getInt("salary"));
 56	
 57	 customers.add(customer);
 58	 System.out.println("DEBUG: Found customer: " + customer);
 59	 }
 60	 }
 61	 }
 62	
 63	 System.out.println("DEBUG: Total customers found: " + customers.size());
 64	 return customers;
 65	 }
