 181	 /**
 182	 * Print report directly to printer
 183	 */
 184	 public static void printReport(List<Customer> customers) {
 185	 try {
 186	 Object jasperPrint = generateReport(customers);
 187	
 188	 // Show confirmation dialog
 189	 Alert confirmAlert = new Alert(Alert.AlertType.CONFIRMATION);
 190	 confirmAlert.setTitle("Print Confirmation");
 191	 confirmAlert.setHeaderText("Print Customer Report");
 192	 confirmAlert.setContentText("Are you sure you want to print the customer report?");
 193	
 194	 Optional<ButtonType> result = confirmAlert.showAndWait();
 195	 if (result.isPresent() && result.get() == ButtonType.OK) {
 196	 // Print the report using reflection
 197	 Class<?> printManagerClass = Class.forName("net.sf.jasperreports.engine.JasperPrintManager");
 198	 var printMethod = printManagerClass.getMethod("printReport",
 199	 Class.forName("net.sf.jasperreports.engine.JasperPrint"), boolean.class);
 200	 printMethod.invoke(null, jasperPrint, true);
 201	
 202	 showInfoAlert("Print Success", "Report sent to printer",
 203	 "The customer report has been sent to the default printer.");
 204	 }
 205	
 206	 } catch (Exception e) {
 207	 showErrorAlert("Print Error", "Failed to print report", e.getMessage());
 208	 }
 209	 }
