 24	 /**
 25	 * Compile the JasperReports template using reflection
 26	 */
 27	 private static void compileReport() throws Exception {
 28	 if (compiledReport == null) {
 29	 InputStream reportStream = JasperReportsUtil.class.getResourceAsStream(REPORT_TEMPLATE);
 30	 if (reportStream == null) {
 31	 throw new Exception("Report template not found: " + REPORT_TEMPLATE);
 32	 }
 33	
 34	 // Use reflection to compile report
 35	 Class<?> compileManagerClass = Class.forName("net.sf.jasperreports.engine.JasperCompileManager");
 36	 var compileMethod = compileManagerClass.getMethod("compileReport", InputStream.class);
 37	 compiledReport = compileMethod.invoke(null, reportStream);
 38	 }
 39	 }
 40	
 41	 /**
 42	 * Generate JasperPrint object from customer data using reflection
 43	 */
 44	 public static Object generateReport(List<Customer> customers) throws Exception {
 45	 compileReport();
 46	
 47	 // Convert customers to Map format for easier handling
 48	 List<Map<String, Object>> customerMaps = new ArrayList<>();
 49	 for (Customer customer : customers) {
 50	 Map<String, Object> customerMap = new HashMap<>();
 51	 customerMap.put("idx", customer.getIdx());
 52	 customerMap.put("nik", customer.getNik());
 53	 customerMap.put("name", customer.getName());
 54	 customerMap.put("born", customer.getBorn() != null ? customer.getBorn().toString() : "");
 55	 customerMap.put("active", customer.isActive());
 56	 customerMap.put("salary", customer.getSalary());
 57	 customerMaps.add(customerMap);
 58	 }
 59	
 60	 // Create data source using reflection
 61	 Class<?> dataSourceClass = Class.forName("net.sf.jasperreports.engine.data.JRBeanCollectionDataSource");
 62	 var dataSourceConstructor = dataSourceClass.getConstructor(java.util.Collection.class);
 63	 Object dataSource = dataSourceConstructor.newInstance(customerMaps);
 64	
 65	 // Create parameters map
 66	 Map<String, Object> parameters = new HashMap<>();
 67	 parameters.put("ReportTitle", "Customer Management Report");
 68	 parameters.put("GeneratedBy", "Customer Management System");
 69	 parameters.put("TotalRecords", customers.size());
 70	
 71	 // Fill the report using reflection
 72	 Class<?> fillManagerClass = Class.forName("net.sf.jasperreports.engine.JasperFillManager");
 73	 var fillMethod = fillManagerClass.getMethod("fillReport",
 74	 Class.forName("net.sf.jasperreports.engine.JasperReport"),
 75	 Map.class,
 76	 Class.forName("net.sf.jasperreports.engine.JRDataSource"));
 77	
 78	 return fillMethod.invoke(null, compiledReport, parameters, dataSource);
 79	 }
