 13	public class CustomerController {
 14	 private final CustomerDAO customerDAO;
 15	 private final CSVHandler csvHandler;
 16	 private final ExcelHandler excelHandler;
 17	 
 18	 public CustomerController() {
 19	 this.customerDAO = new CustomerDAO();
 20	 this.csvHandler = new CSVHandler();
 21	 this.excelHandler = new ExcelHandler();
 22	 }
 23	 
 24	 public List<Customer> getCustomers(int limit, int offset, String searchTerm) throws SQLException {
 25	 return customerDAO.getAllCustomers(limit, offset, searchTerm);
 26	 }
 27	 
 28	 public int getTotalCustomers(String searchTerm) throws SQLException {
 29	 return customerDAO.getTotalCustomers(searchTerm);
 30	 }
 31	 
 32	 public void addCustomer(Customer customer) throws SQLException {
 33	 customerDAO.addCustomer(customer);
 34	 }
 35	 
 36	 public void updateCustomer(Customer customer) throws SQLException {
 37	 customerDAO.updateCustomer(customer);
 38	 }
 39	 
 40	 public void deleteCustomer(int idx) throws SQLException {
 41	 customerDAO.deleteCustomer(idx);
 42	 }
 43	 
 44	 public Map<String, Integer> importCSV(File file) {
 45	 return csvHandler.importCSV(file);
 46	 }
 47	 
 48	 public Map<String, Integer> exportCSV(File file, List<Customer> customers) {
 49	 return csvHandler.exportCSV(file, customers);
 50	 }
 51	 
 52	 public Map<String, Integer> importExcel(File file) {
 53	 return excelHandler.importExcel(file);
 54	 }
 55	 
 56	 public Map<String, Integer> exportExcel(File file, List<Customer> customers) {
 57	 return excelHandler.exportExcel(file, customers);
