 152	 /**
 153	 * Print report directly to printer
 154	 */
 155	 public static void printReport(List<Customer> customers) {
 156	 try {
 157	 JasperPrint jasperPrint = generateReport(customers);
 158	 
 159	 // Show confirmation dialog
 160	 Alert confirmAlert = new Alert(Alert.AlertType.CONFIRMATION);
 161	 confirmAlert.setTitle("Print Confirmation");
 162	 confirmAlert.setHeaderText("Print Customer Report");
 163	 confirmAlert.setContentText("Are you sure you want to print the customer report?");
 164	 
 165	 Optional<ButtonType> result = confirmAlert.showAndWait();
 166	 if (result.isPresent() && result.get() == ButtonType.OK) {
 167	 // Print the report
 168	 JasperPrintManager.printReport(jasperPrint, true);
 169	 
 170	 showInfoAlert("Print Success", "Report sent to printer", 
 171	 "The customer report has been sent to the default printer.");
 172	 }
