 715	 private void showAlert(Alert.AlertType alertType, String title, String header, String content) {
 716	 Alert alert = new Alert(alertType);
 717	 alert.setTitle(title);
 718	 alert.setHeaderText(header);
 719	 alert.setContentText(content);
 720	 alert.showAndWait();
 721	 }
