 215	 private void loadData() {
 216	 try {
 217	 int offset = (currentPage - 1) * rowsPerPage;
 218	 List<Customer> customers = controller.getCustomers(rowsPerPage, offset, currentSearch);
 219	 totalRecords = controller.getTotalCustomers(currentSearch);
