 502	 private void showAddCustomerDialog() {
 503	 Dialog<Customer> dialog = new Dialog<>();
 504	 dialog.setTitle("Add New Customer");
 505	 dialog.setHeaderText("Enter customer details");
 506	 
 507	 // Set the button types
 508	 ButtonType saveButtonType = new ButtonType("Save", ButtonBar.ButtonData.OK_DONE);
 509	 dialog.getDialogPane().getButtonTypes().addAll(saveButtonType, ButtonType.CANCEL);
 510	 
 511	 // Create the form grid
 512	 GridPane grid = new GridPane();
 513	 grid.setHgap(10);
 514	 grid.setVgap(10);
 515	 grid.setPadding(new Insets(20, 150, 10, 10));
 516	 
 517	 TextField nikField = new TextField();
 518	 TextField nameField = new TextField();
 519	 DatePicker bornDatePicker = new DatePicker();
 520	 CheckBox activeCheckBox = new CheckBox();
 521	 TextField salaryField = new TextField();
 522	 
 523	 grid.add(new Label("NIK:"), 0, 0);
 524	 grid.add(nikField, 1, 0);
 525	 grid.add(new Label("Name:"), 0, 1);
 526	 grid.add(nameField, 1, 1);
 527	 grid.add(new Label("Born Date:"), 0, 2);
 528	 grid.add(bornDatePicker, 1, 2);
 529	 grid.add(new Label("Active:"), 0, 3);
 530	 grid.add(activeCheckBox, 1, 3);
 531	 grid.add(new Label("Salary:"), 0, 4);
 532	 grid.add(salaryField, 1, 4);
 533	 
 534	 dialog.getDialogPane().setContent(grid);
 535	 
 536	 // Request focus on the nik field by default
 537	 Platform.runLater(nikField::requestFocus);
 538	 
 539	 // Convert the result to a customer when the save button is clicked
 540	 dialog.setResultConverter(dialogButton -> {
 541	 if (dialogButton == saveButtonType) {
 542	 try {
 543	 Customer customer = new Customer();
 544	 customer.setNik(nikField.getText());
 545	 customer.setName(nameField.getText());
 546	 customer.setBorn(bornDatePicker.getValue());
 547	 customer.setActive(activeCheckBox.isSelected());
 548	 customer.setSalary(Integer.parseInt(salaryField.getText()));
 549	 return customer;
 550	 } catch (NumberFormatException e) {
 551	 showAlert(Alert.AlertType.ERROR, "Input Error", "Invalid salary format", "Please enter a valid number for salary.");
 552	 return null;
 553	 }
 554	 }
 555	 return null;
 556	 });
 557	 
 558	 Optional<Customer> result = dialog.showAndWait();
 559	 
 560	 result.ifPresent(customer -> {
 561	 try {
 562	 controller.addCustomer(customer);
 563	 showAlert(Alert.AlertType.INFORMATION, "Success", "Customer Added", "Customer has been successfully added.");
 564	 loadData();
 565	 } catch (SQLException e) {
 566	 showAlert(Alert.AlertType.ERROR, "Database Error", "Failed to add customer", e.getMessage());
 567	 }
 568	 });
 569	 }
