 150	 /**
 151	 * Helper method to export JasperPrint to PDF file using reflection
 152	 */
 153	 private static void exportToPDFFile(Object jasperPrint, File file) throws Exception {
 154	 // Export to PDF using reflection
 155	 Class<?> pdfExporterClass = Class.forName("net.sf.jasperreports.engine.export.JRPdfExporter");
 156	 Object exporter = pdfExporterClass.getDeclaredConstructor().newInstance();
 157	
 158	 // Set exporter input
 159	 Class<?> simpleExporterInputClass = Class.forName("net.sf.jasperreports.export.SimpleExporterInput");
 160	 var inputConstructor = simpleExporterInputClass.getConstructor(Class.forName("net.sf.jasperreports.engine.JasperPrint"));
 161	 Object exporterInput = inputConstructor.newInstance(jasperPrint);
 162	
 163	 var setExporterInputMethod = pdfExporterClass.getMethod("setExporterInput",
 164	 Class.forName("net.sf.jasperreports.export.ExporterInput"));
 165	 setExporterInputMethod.invoke(exporter, exporterInput);
 166	
 167	 // Set exporter output
 168	 Class<?> simpleOutputStreamExporterOutputClass = Class.forName("net.sf.jasperreports.export.SimpleOutputStreamExporterOutput");
 169	 var outputConstructor = simpleOutputStreamExporterOutputClass.getConstructor(java.io.OutputStream.class);
 170	 Object exporterOutput = outputConstructor.newInstance(new FileOutputStream(file));
 171	
 172	 var setExporterOutputMethod = pdfExporterClass.getMethod("setExporterOutput",
 173	 Class.forName("net.sf.jasperreports.export.ExporterOutput"));
 174	 setExporterOutputMethod.invoke(exporter, exporterOutput);
 175	
 176	 // Export report
 177	 var exportReportMethod = pdfExporterClass.getMethod("exportReport");
 178	 exportReportMethod.invoke(exporter);
 179	 }
