 132	 fileChooser.getExtensionFilters().add(
 133	 new FileChooser.ExtensionFilter("PDF Files", "*.pdf")
 134	 );
 135	
 136	 File file = fileChooser.showSaveDialog(parentStage);
 137	 if (file != null) {
 138	 exportToPDFFile(jasperPrint, file);
 139	
 140	 // Show success message
 141	 showInfoAlert("Export Success", "PDF report has been saved successfully!",
 142	 "File saved to: " + file.getAbsolutePath());
 143	 }
...
 149	
 150	 /**
 151	 * Helper method to export JasperPrint to PDF file using reflection
 152	 */
 153	 private static void exportToPDFFile(Object jasperPrint, File file) throws Exception {
 154	 // Export to PDF using reflection
 155	 Class<?> pdfExporterClass = Class.forName("net.sf.jasperreports.engine.export.JRPdfExporter");
 156	 Object exporter = pdfExporterClass.getDeclaredConstructor().newInstance();
 157	
 158	 // Set exporter input
