 1	package com.tama.jfxcrud.util;
 2	
 3	import java.sql.Connection;
 4	import java.sql.DriverManager;
 5	import java.sql.SQLException;
 6	
 7	public class DatabaseConnection {
 8	 private static String URL = "jdbc:mysql://localhost:3306/test";
 9	 private static String USER = "rangga";
 10	 private static String PASSWORD = "rangga";
 11	 
 12	 private static Connection connection;
 13	 
 14	 public static Connection getConnection() throws SQLException {
 15	 if (connection == null || connection.isClosed() || !connection.isValid(5)) {
 16	 System.out.println("DEBUG: Creating new connection to " + URL);
 17	 connection = DriverManager.getConnection(URL, USER, PASSWORD);
 18	 }
 19	 return connection;
 20	 }
 21	 
 22	 public static void closeConnection() {
 23	 if (connection != null) {
 24	 try {
 25	 connection.close();
 26	 connection = null;
 27	 } catch (SQLException e) {
 28	 e.printStackTrace();
 29	 }
 30	 }
 31	 }
 32	 
 33	 public static void setConnectionParams(String host, String port, String database, String user, String password) {
 34	 URL = String.format("jdbc:mysql://%s:%s/%s", host, port, database);
 35	 USER = user;
 36	 PASSWORD = password;
 37	 // Close existing connection if any
 38	 closeConnection();
 39	 }
 40	
 41	 public static boolean establishConnection() {
 42	 try {
 43	 connection = DriverManager.getConnection(URL, USER, PASSWORD);
 44	 System.out.println("DEBUG: Connection established successfully to " + URL);
 45	 return true;
 46	 } catch (SQLException e) {
 47	 System.err.println("DEBUG: Failed to establish connection: " + e.getMessage());
 48	 return false;
 49	 }
 50	 }
 51	 
 52	 public static boolean testConnection(String host, String port, String database, String user, String password) {
 53	 String testUrl = String.format("jdbc:mysql://%s:%s/%s", host, port, database);
 54	 try (Connection testConn = DriverManager.getConnection(testUrl, user, password)) {
 55	 return true;
 56	 } catch (SQLException e) {
 57	 return false;
 58	 }
 59	 }
 60	 
 61	 public static boolean isConnected() {
 62	 if (connection == null) {
 63	 System.out.println("DEBUG: isConnected() - connection is null");
 64	 return false;
 65	 }
 66	
 67	 try {
 68	 boolean closed = connection.isClosed();
 69	 boolean valid = connection.isValid(5); // 5 second timeout
 70	
 71	 System.out.println("DEBUG: isConnected() - closed: " + closed + ", valid: " + valid);
 72	
 73	 return !closed && valid;
 74	 } catch (SQLException e) {
 75	 System.err.println("DEBUG: isConnected() - SQLException: " + e.getMessage());
 76	 return false;
 77	 }
 78	 }
 79	}
