 62	 /**
 63	 * Generate JasperPrint object from customer data using reflection
 64	 */
 65	 public static Object generateReport(List<Customer> customers) throws Exception {
 66	 compileReport();
 67	
 68	 // Convert customers to Map format for easier handling
 69	 List<Map<String, Object>> customerMaps = new ArrayList<>();
 70	 for (Customer customer : customers) {
 71	 Map<String, Object> customerMap = new HashMap<>();
 72	 customerMap.put("idx", customer.getIdx());
 73	 customerMap.put("nik", customer.getNik());
 74	 customerMap.put("name", customer.getName());
 75	 customerMap.put("born", customer.getBorn() != null ? customer.getBorn().toString() : "");
 76	 customerMap.put("active", customer.isActive());
 77	 customerMap.put("salary", customer.getSalary());
 78	 customerMaps.add(customerMap);
 79	 }
 80	
 81	 // Create data source using reflection
 82	 Class<?> dataSourceClass = Class.forName("net.sf.jasperreports.engine.data.JRBeanCollectionDataSource");
 83	 var dataSourceConstructor = dataSourceClass.getConstructor(java.util.Collection.class);
 84	 Object dataSource = dataSourceConstructor.newInstance(customerMaps);
 85	
 86	 // Create parameters map
 87	 Map<String, Object> parameters = new HashMap<>();
 88	 parameters.put("ReportTitle", "Customer Management Report");
 89	 parameters.put("GeneratedBy", "Customer Management System");
 90	 parameters.put("TotalRecords", customers.size());
 91	
