 101	 public void addCustomer(Customer customer) throws SQLException {
 102	 String query = "INSERT INTO customer (nik, name, born, active, salary) VALUES (?, ?, ?, ?, ?)";
 103	 
 104	 Connection conn = DatabaseConnection.getConnection();
 105	 try (PreparedStatement stmt = conn.prepareStatement(query)) {
 106	 
 107	 stmt.setString(1, customer.getNik());
 108	 stmt.setString(2, customer.getName());
 109	 stmt.setDate(3, customer.getBorn() != null ? Date.valueOf(customer.getBorn()) : null);
 110	 stmt.setBoolean(4, customer.isActive());
 111	 stmt.setInt(5, customer.getSalary());
 112	 
 113	 stmt.executeUpdate();
 114	 }
 115	 }
