 118	 String query = "UPDATE customer SET nik = ?, name = ?, born = ?, active = ?, salary = ? WHERE idx = ?";
 119	 
 120	 Connection conn = DatabaseConnection.getConnection();
 121	 try (PreparedStatement stmt = conn.prepareStatement(query)) {
 122	 
 123	 stmt.setString(1, customer.getNik());
 124	 stmt.setString(2, customer.getName());
