 25	 private static void compileReport() throws Exception {
 26	 if (compiledReport == null) {
 27	 System.out.println("DEBUG: Looking for report template: " + REPORT_TEMPLATE);
 28	 InputStream reportStream = JasperReportsUtil.class.getResourceAsStream(REPORT_TEMPLATE);
 29	 if (reportStream == null) {
 30	 System.err.println("DEBUG: Report template not found: " + REPORT_TEMPLATE);
 31	 // Try alternative path
 32	 reportStream = JasperReportsUtil.class.getClassLoader().getResourceAsStream("reports/customer_report_simple.jrxml");
 33	 if (reportStream == null) {
 34	 throw new Exception("Report template not found: " + REPORT_TEMPLATE);
 35	 }
...
