 41	 try {
 42	 // Use reflection to compile report
 43	 System.out.println("DEBUG: Starting report compilation...");
 44	 Class<?> compileManagerClass = Class.forName("net.sf.jasperreports.engine.JasperCompileManager");
 45	 var compileMethod = compileManagerClass.getMethod("compileReport", InputStream.class);
 46	 compiledReport = compileMethod.invoke(null, reportStream);
 47	 System.out.println("DEBUG: Report compilation successful");
 48	 } catch (Exception e) {
 49	 System.err.println("DEBUG: Report compilation failed: " + e.getMessage());
 50	 if (e.getCause() != null) {
 51	 System.err.println("DEBUG: Root cause: " + e.getCause().getMessage());
 52	 }
 53	 throw e;
 54	 } finally {
 55	 if (reportStream != null) {
 56	 reportStream.close();
 57	 }
 58	 }
 59	 }
 60	 }
