 12	 public List<Customer> getAllCustomers(int limit, int offset, String searchTerm) throws SQLException {
 13	 List<Customer> customers = new ArrayList<>();
 14	 String query = "SELECT * FROM customer WHERE 1=1";
 15	
 16	 if (searchTerm != null && !searchTerm.isEmpty()) {
 17	 query += " AND (nik LIKE ? OR name LIKE ? OR born LIKE ? OR CAST(active AS CHAR) LIKE ? OR CAST(salary AS CHAR) LIKE ?)";
