 100	 // Implementasi CRUD lainnya
 101	 public void addCustomer(Customer customer) throws SQLException {
 102	 String query = "INSERT INTO customer (nik, name, born, active, salary) VALUES (?, ?, ?, ?, ?)";
 103	 
 104	 Connection conn = DatabaseConnection.getConnection();
 105	 try (PreparedStatement stmt = conn.prepareStatement(query)) {
 106	 
 107	 stmt.setString(1, customer.getNik());
 108	 stmt.setString(2, customer.getName());
 109	 stmt.setDate(3, customer.getBorn() != null ? Date.valueOf(customer.getBorn()) : null);
 110	 stmt.setBoolean(4, customer.isActive());
 111	 stmt.setInt(5, customer.getSalary());
 112	 
 113	 stmt.executeUpdate();
 114	 }
 115	 }
 116	 
 117	 public void updateCustomer(Customer customer) throws SQLException {
 118	 String query = "UPDATE customer SET nik = ?, name = ?, born = ?, active = ?, salary = ? WHERE idx = ?";
 119	 
 120	 Connection conn = DatabaseConnection.getConnection();
 121	 try (PreparedStatement stmt = conn.prepareStatement(query)) {
 122	 
 123	 stmt.setString(1, customer.getNik());
 124	 stmt.setString(2, customer.getName());
 125	 stmt.setDate(3, customer.getBorn() != null ? Date.valueOf(customer.getBorn()) : null);
 126	 stmt.setBoolean(4, customer.isActive());
 127	 stmt.setInt(5, customer.getSalary());
 128	 stmt.setInt(6, customer.getIdx());
 129	 
 130	 stmt.executeUpdate();
 131	 }
 132	 }
