 32	// SESUDAH:
 33	public static boolean isConnected() {
 34	 if (connection == null) return false;
 35	 try {
 36	 boolean closed = connection.isClosed();
 37	 boolean valid = connection.isValid(5); // 5 second timeout
 38	 return !closed && valid;
 39	 } catch (SQLException e) {
