 444	 fileChooser.getExtensionFilters().add(new FileChooser.ExtensionFilter("Excel Files", "*.xlsx"));
 445	 File file = fileChooser.showOpenDialog(primaryStage);
 446	 
 447	 if (file != null) {
 448	 Map<String, Integer> result = controller.importExcel(file);
 449	 showAlert(Alert.AlertType.INFORMATION, "Import Result", "Excel Import Completed", 
 450	 String.format("Successfully imported: %d records\nFailed to import: %d records", 
 451	 result.get("success"), result.get("failed")));
 452	 loadData();
 453	 }
 454	 }
