 523	 private void showAddCustomerDialog() {
 524	 Dialog<Customer> dialog = new Dialog<>();
 525	 dialog.setTitle("Add New Customer");
 526	 dialog.setHeaderText("Enter customer details");
 527	 
 528	 // Set the button types
 529	 ButtonType saveButtonType = new ButtonType("Save", ButtonBar.ButtonData.OK_DONE);
 530	 dialog.getDialogPane().getButtonTypes().addAll(saveButtonType, ButtonType.CANCEL);
 531	 
 532	 // Create the form grid
 533	 GridPane grid = new GridPane();
 534	 grid.setHgap(10);
 535	 grid.setVgap(10);
 536	 grid.setPadding(new Insets(20, 150, 10, 10));
 537	 
 538	 TextField nikField = new TextField();
 539	 TextField nameField = new TextField();
 540	 DatePicker bornDatePicker = new DatePicker();
 541	 CheckBox activeCheckBox = new CheckBox();
 542	 TextField salaryField = new TextField();
 543	 
 544	 grid.add(new Label("NIK:"), 0, 0);
 545	 grid.add(nikField, 1, 0);
 546	 grid.add(new Label("Name:"), 0, 1);
 547	 grid.add(nameField, 1, 1);
 548	 grid.add(new Label("Born Date:"), 0, 2);
 549	 grid.add(bornDatePicker, 1, 2);
 550	 grid.add(new Label("Active:"), 0, 3);
 551	 grid.add(activeCheckBox, 1, 3);
 552	 grid.add(new Label("Salary:"), 0, 4);
 553	 grid.add(salaryField, 1, 4);
 554	 
 555	 dialog.getDialogPane().setContent(grid);
 556	 
 557	 // Request focus on the nik field by default
 558	 Platform.runLater(nikField::requestFocus);
 559	 
 560	 // Convert the result to a customer when the save button is clicked
 561	 dialog.setResultConverter(dialogButton -> {
 562	 if (dialogButton == saveButtonType) {
 563	 try {
 564	 Customer customer = new Customer();
 565	 customer.setNik(nikField.getText());
 566	 customer.setName(nameField.getText());
 567	 customer.setBorn(bornDatePicker.getValue());
 568	 customer.setActive(activeCheckBox.isSelected());
 569	 customer.setSalary(Integer.parseInt(salaryField.getText()));
 570	 return customer;
 571	 } catch (NumberFormatException e) {
 572	 showAlert(Alert.AlertType.ERROR, "Input Error", "Invalid salary format", "Please enter a valid number for salary.");
 573	 return null;
 574	 }
 575	 }
 576	 return null;
 577	 });
 578	 
 579	 Optional<Customer> result = dialog.showAndWait();
 580	 
 581	 result.ifPresent(customer -> {
 582	 try {
 583	 controller.addCustomer(customer);
 584	 showAlert(Alert.AlertType.INFORMATION, "Success", "Customer Added", "Customer has been successfully added.");
 585	 loadData();
 586	 } catch (SQLException e) {
 587	 showAlert(Alert.AlertType.ERROR, "Database Error", "Failed to add customer", e.getMessage());
 588	 }
 589	 });
 590	 }
