 746	 @FXML
 747	 private void handlePrintPreview() {
 748	 System.out.println("DEBUG: ========== PRINT PREVIEW CLICKED ==========");
 749	
 750	 if (!DatabaseConnection.isConnected()) {
 751	 System.out.println("DEBUG: Database not connected for print preview");
 752	 showNotConnectedAlert();
 753	 return;
 754	 }
 755	
 756	 try {
 757	 System.out.println("DEBUG: Loading customers for print preview...");
 758	 System.out.println("DEBUG: Current search term: '" + currentSearch + "'");
 759	
 760	 // Get customers based on current search filter
 761	 List<Customer> filteredCustomers;
 762	 if (currentSearch == null || currentSearch.trim().isEmpty()) {
 763	 // If no search, get all customers
 764	 filteredCustomers = controller.getCustomers(totalRecords, 0, "");
 765	 System.out.println("DEBUG: No search filter - loading all " + filteredCustomers.size() + " customers");
 766	 } else {
 767	 // If there's a search, get filtered customers (use a large limit to get all filtered results)
 768	 filteredCustomers = controller.getCustomers(10000, 0, currentSearch);
 769	 System.out.println("DEBUG: Search filter '" + currentSearch + "' - loading " + filteredCustomers.size() + " customers");
 770	 }
 771	
 772	 if (filteredCustomers.isEmpty()) {
 773	 System.out.println("DEBUG: No customers found for report");
 774	 showAlert(Alert.AlertType.INFORMATION, "No Data", "No Data to Preview",
 775	 "There are no customers to display in the report based on current filter.");
 776	 return;
 777	 }
 778	
 779	 System.out.println("DEBUG: Calling JasperReportsUtil.showPrintPreview...");
 780	 // Show print preview
 781	 JasperReportsUtil.showPrintPreview(filteredCustomers, primaryStage);
 782	 System.out.println("DEBUG: Print preview completed");
 783	
 784	 } catch (SQLException e) {
 785	 System.err.println("DEBUG: SQL Error in print preview: " + e.getMessage());
 786	 e.printStackTrace();
 787	 showAlert(Alert.AlertType.ERROR, "Database Error", "Failed to load data for preview", e.getMessage());
 788	 } catch (Exception e) {
 789	 System.err.println("DEBUG: General Error in print preview: " + e.getMessage());
 790	 e.printStackTrace();
 791	 showAlert(Alert.AlertType.ERROR, "Error", "Failed to generate preview", e.getMessage());
 792	 }
 793	 }
