 242	 
 243	 @FXML
 244	 private void handleRefreshTable() {
 245	 System.out.println("DEBUG: ========== REFRESH TABLE CLICKED ==========");
 246	
 247	 try {
 248	 // 1. Validasi Koneksi Database
 249	 System.out.println("DEBUG: Checking database connection...");
 250	 if (!DatabaseConnection.isConnected()) {
 251	 System.out.println("DEBUG: Cannot refresh - database not connected");
 252	 showNotConnectedAlert();
 253	 return;
 254	 }
 255	 System.out.println("DEBUG: Database connection verified");
 256	
 257	 // 2. Reset State ke Kondisi Awal
 258	 System.out.println("DEBUG: Resetting state to initial conditions...");
 259	 currentPage = 1;
 260	 currentSearch = "";
 261	
 262	 // Clear search field
 263	 if (searchField != null) {
 264	 searchField.clear();
 265	 System.out.println("DEBUG: Search field cleared");
 266	 }
 267	
 268	 // Reset rows per page to default
 269	 if (rowsPerPageComboBox != null) {
 270	 rowsPerPageComboBox.setValue(10);
 271	 rowsPerPage = 10;
 272	 System.out.println("DEBUG: Rows per page reset to default (10)");
 273	 }
 274	
 275	 // 3. Reload Data dari Database
 276	 System.out.println("DEBUG: Reloading data from database...");
 277	 loadData();
 278	
 279	 // 4. Update UI Components (additional refresh)
 280	 System.out.println("DEBUG: Refreshing UI components...");
 281	 if (tableView != null) {
 282	 tableView.refresh();
 283	 System.out.println("DEBUG: Table view refreshed");
 284	 }
 285	
 286	 System.out.println("DEBUG: Table refresh completed successfully");
 287	
 288	 } catch (Exception e) {
 289	 // 5. Error Handling
 290	 System.err.println("DEBUG: Error during table refresh: " + e.getMessage());
 291	 e.printStackTrace();
 292	 showAlert(Alert.AlertType.ERROR, "Refresh Error",
 293	 "Failed to refresh table",
 294	 "An error occurred while refreshing the table: " + e.getMessage());
 295	 }
 296	 }
