 298	 @FXML
 299	 private void handlePrevPage() {
 300	 System.out.println("DEBUG: ========== PREVIOUS BUTTON CLICKED ==========");
 301	 System.out.println("DEBUG: Current page: " + currentPage + ", Total pages: " + totalPages);
 302	 System.out.println("DEBUG: Database connected: " + DatabaseConnection.isConnected());
 303	
 304	 if (!DatabaseConnection.isConnected()) {
 305	 System.out.println("DEBUG: Cannot navigate - database not connected");
 306	 showNotConnectedAlert();
 307	 return;
 308	 }
 309	
 310	 if (currentPage > 1) {
 311	 currentPage--;
 312	 System.out.println("DEBUG: Moving to page: " + currentPage);
 313	 loadData();
 314	 } else {
 315	 System.out.println("DEBUG: Already on first page");
 316	 }
 317	 }
