 320	 private void handleNextPage() {
 321	 System.out.println("DEBUG: ========== NEXT BUTTON CLICKED ==========");
 322	 System.out.println("DEBUG: Current page: " + currentPage + ", Total pages: " + totalPages);
 323	 System.out.println("DEBUG: Database connected: " + DatabaseConnection.isConnected());
 324	
 325	 if (!DatabaseConnection.isConnected()) {
 326	 System.out.println("DEBUG: Cannot navigate - database not connected");
 327	 showNotConnectedAlert();
 328	 return;
 329	 }
 330	
 331	 if (currentPage < totalPages) {
 332	 currentPage++;
 333	 System.out.println("DEBUG: Moving to page: " + currentPage);
