 340	 @FXML
 341	 private void handleRowsPerPageChange() {
 342	 System.out.println("DEBUG: ========== ROWS PER PAGE CHANGED VIA FXML ==========");
 343	
 344	 if (rowsPerPageComboBox == null) {
 345	 System.err.println("DEBUG: ERROR - rowsPerPageComboBox is null in handler!");
 346	 return;
 347	 }
 348	
 349	 Integer selectedValue = rowsPerPageComboBox.getValue();
 350	 System.out.println("DEBUG: Selected value: " + selectedValue);
 351	
 352	 if (selectedValue != null) {
 353	 System.out.println("DEBUG: Changing rows per page from " + rowsPerPage + " to " + selectedValue);
 354	 rowsPerPage = selectedValue;
 355	 currentPage = 1;
 356	
 357	 if (DatabaseConnection.isConnected()) {
 358	 loadData();
 359	 } else {
 360	 System.out.println("DEBUG: Cannot load data - database not connected");
 361	 }
 362	 } else {
 363	 System.out.println("DEBUG: Selected value is null");
 364	 }
 365	 }
