 481	 
 482	 private void loadData() {
 483	 if (!DatabaseConnection.isConnected()) {
 484	 System.out.println("DEBUG: Database not connected");
 485	 return;
 486	 }
 487	
 488	 try {
 489	 int offset = (currentPage - 1) * rowsPerPage;
 490	 System.out.println("DEBUG: Loading data - Page: " + currentPage + ", RowsPerPage: " + rowsPerPage + ", Offset: " + offset + ", Search: '" + currentSearch + "'");
 491	
 492	 List<Customer> customers = controller.getCustomers(rowsPerPage, offset, currentSearch);
 493	 totalRecords = controller.getTotalCustomers(currentSearch);
 494	 totalPages = totalRecords > 0 ? (int) Math.ceil((double) totalRecords / rowsPerPage) : 1;
 495	
 496	 System.out.println("DEBUG: Retrieved " + customers.size() + " customers, Total records: " + totalRecords + ", Total pages: " + totalPages);
 497	
 498	 ObservableList<Customer> data = FXCollections.observableArrayList(customers);
 499	 tableView.setItems(data);
 500	
 501	 updatePaginationControls();
 502	 } catch (SQLException e) {
 503	 System.err.println("DEBUG: SQL Error: " + e.getMessage());
 504	 e.printStackTrace();
 505	 showAlert(Alert.AlertType.ERROR, "Database Error", "Failed to load customer data", e.getMessage());
 506	 }
 507	 }
 508	 
 509	 private void updatePaginationControls() {
 510	 String pageText = String.format("Page %d of %d (Total records: %d)", currentPage, totalPages, totalRecords);
 511	 pageInfoLabel.setText(pageText);
 512	
 513	 boolean prevDisabled = currentPage <= 1;
 514	 boolean nextDisabled = currentPage >= totalPages || totalPages == 0;
 515	
 516	 prevButton.setDisable(prevDisabled);
 517	 nextButton.setDisable(nextDisabled);
 518	
 519	 System.out.println("DEBUG: Pagination updated - " + pageText);
 520	 System.out.println("DEBUG: Previous button disabled: " + prevDisabled + ", Next button disabled: " + nextDisabled);
 521	 }
