 592	 private void showCustomerDetailDialog(Customer customer) {
 593	 Dialog<ButtonType> dialog = new Dialog<>();
 594	 dialog.setTitle("Customer Details");
 595	 dialog.setHeaderText("Customer: " + customer.getName());
 596	 
 597	 // Set the button types
 598	 ButtonType editButtonType = new ButtonType("Edit", ButtonBar.ButtonData.LEFT);
 599	 ButtonType deleteButtonType = new ButtonType("Delete", ButtonBar.ButtonData.LEFT);
 600	 dialog.getDialogPane().getButtonTypes().addAll(editButtonType, deleteButtonType, ButtonType.CLOSE);
 601	 
 602	 // Create the details grid
 603	 GridPane grid = new GridPane();
 604	 grid.setHgap(10);
 605	 grid.setVgap(10);
 606	 grid.setPadding(new Insets(20, 150, 10, 10));
 607	 
 608	 grid.add(new Label("ID:"), 0, 0);
 609	 grid.add(new Label(String.valueOf(customer.getIdx())), 1, 0);
 610	 grid.add(new Label("NIK:"), 0, 1);
 611	 grid.add(new Label(customer.getNik()), 1, 1);
 612	 grid.add(new Label("Name:"), 0, 2);
 613	 grid.add(new Label(customer.getName()), 1, 2);
 614	 grid.add(new Label("Born Date:"), 0, 3);
 615	 grid.add(new Label(customer.getBorn() != null ? customer.getBorn().toString() : ""), 1, 3);
 616	 grid.add(new Label("Active:"), 0, 4);
 617	 grid.add(new Label(customer.isActive() ? "Yes" : "No"), 1, 4);
 618	 grid.add(new Label("Salary:"), 0, 5);
 619	 grid.add(new Label(String.format("%,d", customer.getSalary())), 1, 5);
 620	 
 621	 dialog.getDialogPane().setContent(grid);
 622	 
 623	 Optional<ButtonType> result = dialog.showAndWait();
 624	 
 625	 if (result.isPresent()) {
 626	 if (result.get() == editButtonType) {
 627	 showEditCustomerDialog(customer);
 628	 } else if (result.get() == deleteButtonType) {
 629	 showDeleteConfirmation(customer);
 630	 }
 631	 }
 632	 }
