 634	 private void showEditCustomerDialog(Customer customer) {
 635	 Dialog<Customer> dialog = new Dialog<>();
 636	 dialog.setTitle("Edit Customer");
 637	 dialog.setHeaderText("Edit customer details");
 638	 
 639	 // Set the button types
 640	 ButtonType saveButtonType = new ButtonType("Save", ButtonBar.ButtonData.OK_DONE);
 641	 dialog.getDialogPane().getButtonTypes().addAll(saveButtonType, ButtonType.CANCEL);
 642	 
 643	 // Create the form grid
 644	 GridPane grid = new GridPane();
 645	 grid.setHgap(10);
 646	 grid.setVgap(10);
 647	 grid.setPadding(new Insets(20, 150, 10, 10));
 648	 
 649	 TextField nikField = new TextField(customer.getNik());
 650	 TextField nameField = new TextField(customer.getName());
 651	 DatePicker bornDatePicker = new DatePicker(customer.getBorn());
 652	 CheckBox activeCheckBox = new CheckBox();
 653	 activeCheckBox.setSelected(customer.isActive());
 654	 TextField salaryField = new TextField(String.valueOf(customer.getSalary()));
 655	 
 656	 grid.add(new Label("NIK:"), 0, 0);
 657	 grid.add(nikField, 1, 0);
 658	 grid.add(new Label("Name:"), 0, 1);
 659	 grid.add(nameField, 1, 1);
 660	 grid.add(new Label("Born Date:"), 0, 2);
 661	 grid.add(bornDatePicker, 1, 2);
 662	 grid.add(new Label("Active:"), 0, 3);
 663	 grid.add(activeCheckBox, 1, 3);
 664	 grid.add(new Label("Salary:"), 0, 4);
 665	 grid.add(salaryField, 1, 4);
 666	 
 667	 dialog.getDialogPane().setContent(grid);
 668	 
 669	 // Request focus on the name field by default
 670	 Platform.runLater(nameField::requestFocus);
 671	 
 672	 // Convert the result to a customer when the save button is clicked
 673	 dialog.setResultConverter(dialogButton -> {
 674	 if (dialogButton == saveButtonType) {
 675	 try {
 676	 Customer updatedCustomer = new Customer();
 677	 updatedCustomer.setIdx(customer.getIdx());
 678	 updatedCustomer.setNik(nikField.getText());
 679	 updatedCustomer.setName(nameField.getText());
 680	 updatedCustomer.setBorn(bornDatePicker.getValue());
 681	 updatedCustomer.setActive(activeCheckBox.isSelected());
 682	 updatedCustomer.setSalary(Integer.parseInt(salaryField.getText()));
 683	 return updatedCustomer;
 684	 } catch (NumberFormatException e) {
 685	 showAlert(Alert.AlertType.ERROR, "Input Error", "Invalid salary format", "Please enter a valid number for salary.");
 686	 return null;
 687	 }
 688	 }
 689	 return null;
 690	 });
 691	 
 692	 Optional<Customer> result = dialog.showAndWait();
 693	 
 694	 result.ifPresent(updatedCustomer -> {
 695	 Alert confirmAlert = new Alert(Alert.AlertType.CONFIRMATION);
 696	 confirmAlert.setTitle("Confirm Update");
 697	 confirmAlert.setHeaderText("Update Customer");
 698	 confirmAlert.setContentText("Are you sure you want to update this customer?");
 699	 
 700	 Optional<ButtonType> confirmResult = confirmAlert.showAndWait();
 701	 if (confirmResult.isPresent() && confirmResult.get() == ButtonType.OK) {
 702	 try {
 703	 controller.updateCustomer(updatedCustomer);
 704	 showAlert(Alert.AlertType.INFORMATION, "Success", "Customer Updated", "Customer has been successfully updated.");
 705	 loadData();
 706	 } catch (SQLException e) {
 707	 showAlert(Alert.AlertType.ERROR, "Database Error", "Failed to update customer", e.getMessage());
 708	 }
 709	 }
 710	 });
 711	 }
