 713	 private void showDeleteConfirmation(Customer customer) {
 714	 Alert alert = new Alert(Alert.AlertType.CONFIRMATION);
 715	 alert.setTitle("Confirm Delete");
 716	 alert.setHeaderText("Delete Customer");
 717	 alert.setContentText("Are you sure you want to delete customer: " + customer.getName() + "?");
 718	 
 719	 Optional<ButtonType> result = alert.showAndWait();
 720	 if (result.isPresent() && result.get() == ButtonType.OK) {
 721	 try {
 722	 controller.deleteCustomer(customer.getIdx());
 723	 showAlert(Alert.AlertType.INFORMATION, "Success", "Customer Deleted", "Customer has been successfully deleted.");
 724	 loadData();
 725	 } catch (SQLException e) {
 726	 showAlert(Alert.AlertType.ERROR, "Database Error", "Failed to delete customer", e.getMessage());
 727	 }
 728	 }
