 1	package com.tama.mysqljfx.util;
 2	
 3	import java.sql.Connection;
 4	import java.sql.DriverManager;
 5	import java.sql.SQLException;
 6	
 7	public class MySQLConnection {
 8	 private static final String URL = "jdbc:mysql://localhost:3306/testing";
 9	 private static final String USER = "rangga";
 10	 private static final String PASSWORD = "rangga";
 11	
 12	 public static Connection createConnection() throws SQLException {
 13	 try {
 14	 Class.forName("com.mysql.cj.jdbc.Driver");
 15	 Connection connection = DriverManager.getConnection(URL, USER, PASSWORD);
 16	 connection.setAutoCommit(false);
 17	 return connection;
 18	 } catch (ClassNotFoundException e) {
 19	 throw new SQLException("Driver MySQL tidak ditemukan: " + e.getMessage());
 20	 } catch (SQLException e) {
 21	 throw new SQLException("Gagal koneksi ke database: " + e.getMessage());
 22	 }
 23	 }
 24	
 25	 // Check connection status
 26	 public static boolean isConnected(Connection connection) {
 27	 try {
 28	 return connection != null && !connection.isClosed();
 29	 } catch (SQLException e) {
 30	 return false;
 31	 }
 32	 }
 33	
 34	 public static String testConnection() {
 35	 Connection connection = null;
 36	 try {
 37	 connection = createConnection();
 38	 if (isConnected(connection)) {
 39	 return "Berhasil terkoneksi ke database";
 40	 } else {
 41	 return "Gagal: Koneksi tidak dapat dibuat";
 42	 }
 43	 } catch (SQLException e) {
 44	 return "Error: " + e.getMessage();
 45	 } finally {
 46	 closeConnection(connection);
 47	 }
 48	 }
 49	
 50	 public static void closeConnection(Connection connection) {
 51	 if (connection != null) {
 52	 try {
 53	 connection.close();
 54	 } catch (SQLException e) {
 55	 System.err.println("Error saat menutup koneksi: " + e.getMessage());
 56	 }
 57	 }
 58	 }
