 15	import javafx.scene.control.*;
 16	
 17	import java.net.URL;
 18	import java.sql.SQLException;
 19	import java.util.ResourceBundle;
 20	
 21	public class MainController implements Initializable {
 22	 @FXML
 23	 private TextField txtFaculty;
 24	 @FXML
 25	 private TableView<Faculty> tableFaculty;
 26	 @FXML
 27	 private TableColumn<Faculty, Integer> facColID;
 28	 @FXML
 29	 private TableColumn<Faculty, String> facColName;
 30	 @FXML
 31	 private TextField txtDepartment;
 32	 @FXML
 33	 private ComboBox<Faculty> cboxFaculty;
 34	 @FXML
 35	 private TableView<Department> tableDepartment;
 36	 @FXML
 37	 private TableColumn<Department, Integer> depColID;
 38	 @FXML
 39	 private TableColumn<Department, String> depColName;
 40	 @FXML
 41	 private TableColumn<Department, Faculty> depColFaculty;
 42	 private ObservableList<Faculty> faculties;
 43	 private ObservableList<Department> departments;
 44	 private FacultyDAOImpl facultyDAO;
 45	 private DepartmentDAOImpl departmentDAO;
 46	
 47	 @FXML
 48	 private void saveFacultyAction(ActionEvent actionEvent) {
 49	 // Check text field is not empty. If empty show alert
 50	 if (txtFaculty.getText().trim().isEmpty()) {
 51	 Alert alert = new Alert(Alert.AlertType.ERROR);
 52	 alert.setContentText("Please fill the field: Faculty Name");
 53	 alert.showAndWait();
 54	 } else {
 55	 Faculty faculty = new Faculty();
 56	 faculty.setName(txtFaculty.getText().trim());
 57	 try {
 58	 int result = facultyDAO.addData(faculty);
 59	 if (result == 1) {
 60	 // Refresh the list by clearing and adding all data from database
 61	 faculties.setAll(facultyDAO.fetchAll());
 62	 txtFaculty.clear();
 63	 }
 64	 } catch (SQLException | ClassNotFoundException e) {
 65	 e.printStackTrace();
 66	 }
 67	 }
 68	 }
 69	 @FXML
 70	 private void saveDepartmentAction(ActionEvent actionEvent) {
 71	 if (txtDepartment.getText().trim().isEmpty()) {
 72	 Alert alert = new Alert(Alert.AlertType.ERROR);
 73	 alert.setContentText("Please fill the field: Department Name");
 74	 alert.showAndWait();
 75	 } else {
 76	 Department department = new Department();
 77	 department.setName(txtDepartment.getText().trim());
 78	 department.setFaculty(cboxFaculty.getValue());
 79	 try {
 80	 int result = departmentDAO.addData(department);
 81	 if (result == 1) {
 82	 departments.addAll(departmentDAO.fetchAll());
 83	 txtDepartment.clear();
 84	 }
 85	 } catch (SQLException | ClassNotFoundException e) {
 86	 e.printStackTrace();
 87	 }
 88	 }
 89	 }
 90	
 91	 @Override
 92	 public void initialize(URL location, ResourceBundle resources) {
 93	 facultyDAO = new FacultyDAOImpl();
 94	 departmentDAO = new DepartmentDAOImpl();
 95	 faculties = FXCollections.observableArrayList();
 96	 departments = FXCollections.observableArrayList();
 97	 try {
 98	 faculties.addAll(facultyDAO.fetchAll());
 99	 departments.addAll(departmentDAO.fetchAll());
 100	 } catch (SQLException | ClassNotFoundException e) {
 101	 e.printStackTrace();
 102	 }
 103	 tableFaculty.setItems(faculties);
 104	 cboxFaculty.setItems(faculties);
 105	 facColName.setCellValueFactory(data -> new SimpleStringProperty(data.getValue().getName()));
 106	 facColID.setCellValueFactory(data -> new SimpleIntegerProperty(data.getValue().getId()).asObject());
 107	
 108	 tableDepartment.setItems(departments);
 109	 depColName.setCellValueFactory(data -> new SimpleStringProperty(data.getValue().getName()));
 110	 depColID.setCellValueFactory(data -> new SimpleIntegerProperty(data.getValue().getId()).asObject());
 111	 depColFaculty.setCellValueFactory(data -> new SimpleObjectProperty<>(data.getValue().getFaculty()));
 112	 }
 113	}
