Path: TrialFX/MySQLJavaFX/src/main/java/com/tama/mysqljfx/dao/DepartmentDAOImpl.java
 1	package com.tama.mysqljfx.dao;
 2	
 3	import com.tama.mysqljfx.entity.Department;
 4	import com.tama.mysqljfx.entity.Faculty;
 5	import com.tama.mysqljfx.util.DAOService;
 6	import com.tama.mysqljfx.util.MySQLConnection;
 7	
 8	import java.sql.Connection;
 9	import java.sql.PreparedStatement;
 10	import java.sql.ResultSet;
 11	import java.sql.SQLException;
 12	import java.util.ArrayList;
 13	import java.util.List;
 14	
 15	public class DepartmentDAOImpl implements DAOService<Department> {
 16	 @Override
 17	 public List<Department> fetchAll() throws SQLException, ClassNotFoundException {
 18	 List<Department> departments = new ArrayList<>();
 19	 try (Connection connection = MySQLConnection.createConnection()) {
 20	 String query = "SELECT dep.id, dep.name, dep.faculty_id, fac.name AS faculty_name FROM department dep JOIN faculty fac ON dep.faculty_id = fac.id";
 21	 try (PreparedStatement preState = connection.prepareStatement(query)) {
 22	 try (ResultSet result = preState.executeQuery()) {
 23	 while (result.next()) {
 24	 Faculty faculty = new Faculty();
 25	 faculty.setId(result.getInt("faculty_id"));
 26	 faculty.setName(result.getString("faculty_name"));
 27	
 28	 Department department = new Department();
 29	 department.setId(result.getInt("id"));
 30	 department.setName(result.getString("name"));
 31	 department.setFaculty(faculty);
 32	 departments.add(department);
 33	 }
 34	 }
 35	 }
 36	 }
 37	 return departments;
 38	 }
 39	
 40	 @Override
 41	 public int addData(Department object) throws SQLException, ClassNotFoundException {
 42	 int result = 0;
 43	 try (Connection connection = MySQLConnection.createConnection()) {
 44	 String query = "INSERT INTO department (name, faculty_id) VALUES (?, ?)";
 45	 try (PreparedStatement preState = connection.prepareStatement(query)) {
 46	 preState.setString(1, object.getName());
 47	 preState.setInt(2, object.getFaculty().getId());
 48	 if (preState.executeUpdate() != 0) {
 49	 connection.commit();
 50	 result = 1;
 51	 } else {
 52	 connection.rollback();
 53	 }
 54	 }
 55	 }
 56	 return result;
 57	 }
 58	}
