Path: TrialFX/MySQLJavaFX/src/main/java/com/tama/mysqljfx/dao/FacultyDAOImpl.java
 1	package com.tama.mysqljfx.dao;
 2	
 3	import com.tama.mysqljfx.entity.Faculty;
 4	import com.tama.mysqljfx.util.DAOService;
 5	import com.tama.mysqljfx.util.MySQLConnection;
 6	
 7	import java.sql.Connection;
 8	import java.sql.PreparedStatement;
 9	import java.sql.ResultSet;
 10	import java.sql.SQLException;
 11	import java.util.ArrayList;
 12	import java.util.List;
 13	
 14	public class FacultyDAOImpl implements DAOService<Faculty> {
 15	
 16	 @Override
 17	 public List<Faculty> fetchAll() throws SQLException, ClassNotFoundException {
 18	 List<Faculty> faculties = new ArrayList<>();
 19	 try (Connection connection = MySQLConnection.createConnection()) {
 20	 String query = "SELECT id, name FROM faculty";
 21	 try (PreparedStatement preState = connection.prepareStatement(query)) {
 22	 try (ResultSet result = preState.executeQuery()) {
 23	 while (result.next()) {
 24	 Faculty faculty = new Faculty();
 25	 faculty.setId(result.getInt("id"));
 26	 faculty.setName(result.getString("name"));
 27	 faculties.add(faculty);
 28	 }
 29	 }
 30	 }
 31	 }
 32	 return faculties;
 33	 }
 34	
 35	 @Override
 36	 public int addData(Faculty object) throws SQLException, ClassNotFoundException {
 37	 int result = 0;
 38	 try (Connection connection = MySQLConnection.createConnection()) {
 39	 String query = "INSERT INTO faculty (name) VALUES (?)";
 40	 try (PreparedStatement preState = connection.prepareStatement(query)) {
 41	 preState.setString(1, object.getName());
 42	 if (preState.executeUpdate() != 0) {
 43	 connection.commit();
 44	 result = 1;
 45	 } else {
 46	 connection.rollback();
 47	 }
 48	 }
 49	 }
 50	 return result;
 51	 }
 52	}
