 1	-- Tabel faculty
 2	CREATE TABLE faculty (
 3	 id INT PRIMARY KEY,
 4	 name VARCHAR(45) NOT NULL,
 5	 created TIMESTAMP DEFAULT CURRENT_TIMESTAMP,
 6	 CHECK (CHAR_LENGTH(name) > 0)
 7	) ENGINE=InnoDB
 8	 DEFAULT CHARSET=utf8mb4
 9	 COLLATE=utf8mb4_unicode_ci;
 10	
 11	-- Tabel department
 12	CREATE TABLE department (
 13	 id INT PRIMARY KEY,
 14	 name VARCHAR(45) NOT NULL,
 15	 created TIMESTAMP DEFAULT CURRENT_TIMESTAMP,
 16	 faculty_id INT DEFAULT NULL COMMENT 'Relasi ke tabel faculty',
 17	 CONSTRAINT fk_faculty
 18	 FOREIGN KEY (faculty_id) REFERENCES faculty(id)
 19	 ON DELETE SET NULL
 20	 ON UPDATE CASCADE,
 21	 CHECK (CHAR_LENGTH(name) > 0),
 22	 INDEX idx_faculty_id (faculty_id)
 23	) ENGINE=InnoDB
 24	 DEFAULT CHARSET=utf8mb4
 25	 COLLATE=utf8mb4_unicode_ci;
