 117	 @Test
 118	 void testAddAllCausesDuplication() throws SQLException, ClassNotFoundException {
 119	 // This test demonstrates the old problematic behavior
 120	 Faculty faculty1 = new Faculty();
 121	 faculty1.setId(1);
 122	 faculty1.setName("Teknik");
 123	
 124	 Faculty faculty2 = new Faculty();
 125	 faculty2.setId(2);
 126	 faculty2.setName("Ekonomi");
 127	
 128	 // Add initial faculty
 129	 faculties.add(faculty1);
 130	
 131	 // Mock DAO to return both faculties
 132	 List<Faculty> allFaculties = Arrays.asList(faculty1, faculty2);
 133	 when(facultyDAO.fetchAll()).thenReturn(allFaculties);
 134	
 135	 // Act: Use addAll (the old problematic way)
 136	 faculties.addAll(facultyDAO.fetchAll());
 137	
 138	 // Assert: This will show duplication
 139	 assertEquals(3, faculties.size(), "addAll causes duplication - should have 3 items (1 duplicate)");
 140	 
 141	 // Count occurrences of "Teknik"
 142	 long teknikCount = faculties.stream()
 143	 .map(Faculty::getName)
 144	 .filter(name -> "Teknik".equals(name))
 145	 .count();
 146	 assertEquals(2, teknikCount, "Teknik appears twice due to duplication");
 147	 }
