 16	
 17	 public static Connection createConnection() throws SQLException {
 18	 try {
 19	 Class.forName("com.mysql.cj.jdbc.Driver");
 20	 Connection connection = DriverManager.getConnection(URL, USER, PASSWORD);
 21	 connection.setAutoCommit(false); // Enable manual transaction management
 22	 return connection;
 23	 } catch (ClassNotFoundException e) {
 24	 throw new SQLException("Driver MySQL tidak ditemukan: " + e.getMessage());
 25	 } catch (SQLException e) {
 26	 throw new SQLException("Gagal koneksi ke database: " + e.getMessage());
 27	 }
 28	 }
