 55	 @FXML
 56	 private void saveFacultyAction(ActionEvent actionEvent) {
 57	 // Check text field is not empty. If empty show alert
 58	 if (txtFaculty.getText().trim().isEmpty()) {
 59	 Alert alert = new Alert(Alert.AlertType.ERROR);
 60	 alert.setContentText("Please fill the field: Faculty Name");
 61	 alert.showAndWait();
 62	 } else {
 63	 Faculty faculty = new Faculty();
 64	 faculty.setName(txtFaculty.getText().trim());
 65	 try {
 66	 int result = facultyDAO.addData(faculty);
 67	 if (result == 1) {
 68	 // Refresh the list by clearing and adding all data from database
 69	 faculties.setAll(facultyDAO.fetchAll());
 70	 txtFaculty.clear();
 71	 }
 72	 } catch (SQLException | ClassNotFoundException e) {
 73	 e.printStackTrace();
 74	 }
 75	 }
 76	 }
 77	 @FXML
 78	 private void saveDepartmentAction(ActionEvent actionEvent) {
 79	 if (txtDepartment.getText().trim().isEmpty()) {
 80	 Alert alert = new Alert(Alert.AlertType.ERROR);
 81	 alert.setContentText("Please fill the field: Department Name");
 82	 alert.showAndWait();
 83	 } else {
 84	 Department department = new Department();
 85	 department.setName(txtDepartment.getText().trim());
 86	 department.setFaculty(cboxFaculty.getValue());
 87	 try {
 88	 int result = departmentDAO.addData(department);
 89	 if (result == 1) {
 90	 // Refresh the list by clearing and adding all data from database
 91	 departments.setAll(departmentDAO.fetchAll());
 92	 txtDepartment.clear();
 93	 }
 94	 } catch (SQLException | ClassNotFoundException e) {
 95	 e.printStackTrace();
 96	 }
 97	 }
 98	 }
