 120	 depColFaculty.setCellValueFactory(data -> new SimpleObjectProperty<>(data.getValue().getFaculty()));
 121	 depColFaculty.setCellFactory(column -> new TableCell<>() {
 122	 @Override
 123	 protected void updateItem(Faculty faculty, boolean empty) {
 124	 super.updateItem(faculty, empty);
 125	 setText(empty || faculty == null ? "" : faculty.getName());
 126	 }
 127	 });
 128	
 129	 // ComboBox setup
 130	 cboxFaculty.setItems(faculties);
 131	 cboxFaculty.setPromptText("Select Faculty");
 132	 }
 133	
 134	 private void loadData() {
