 141	 showErrorAlert("Failed to load data: " + e.getMessage());
 142	 }
 143	 }
 144	
 145	 private void setupTableClickHandlers() {
 146	 tableFaculty.setOnMouseClicked(event -> {
 147	 if (event.getClickCount() == 2) {
 148	 Faculty selectedFaculty = tableFaculty.getSelectionModel().getSelectedItem();
 149	 if (selectedFaculty != null) {
 150	 openFacultyEditDialog(selectedFaculty);
 151	 }
 152	 }
 153	 });
 154	
 155	 tableDepartment.setOnMouseClicked(event -> {
 156	 if (event.getClickCount() == 2) {
 157	 Department selectedDepartment = tableDepartment.getSelectionModel().getSelectedItem();
 158	 if (selectedDepartment != null) {
 159	 openDepartmentEditDialog(selectedDepartment);
 160	 }
 161	 }
 162	 });
 163	 }
 164	
 165	 private void openFacultyEditDialog(Faculty faculty) {
 166	 try {
 167	 FXMLLoader loader = new FXMLLoader(getClass().getResource("/com/tama/mysqljfx/faculty-edit-modal.fxml"));
 168	 Scene scene = new Scene(loader.load());
 169	 Stage stage = new Stage();
 170	 stage.setTitle("Edit Faculty");
 171	 stage.setScene(scene);
 172	 stage.initModality(Modality.APPLICATION_MODAL);
