 174	 FacultyEditController controller = loader.getController();
 175	 controller.setFaculty(faculty);
 176	 controller.setMainController(this);
 177	
 178	 stage.showAndWait();
 179	 } catch (IOException e) {
 180	 showErrorAlert("Failed to open faculty edit window: " + e.getMessage());
 181	 }
