 3	import java.sql.Connection;
 4	import java.sql.DriverManager;
 5	import java.sql.SQLException;
 6	
 7	public class MySQLConnection {
 8	 private static final String URL = "jdbc:mysql://localhost:3306/testing";
 9	 private static final String USER = "rangga";
 10	 private static final String PASSWORD = "rangga"; // TODO: Move to configuration file
 11	
 12	 // Private constructor to prevent instantiation
 13	 private MySQLConnection() {
 14	 throw new IllegalStateException("Utility class");
 15	 }
 16	
 17	 public static Connection createConnection() throws SQLException {
 18	 try {
 19	 Class.forName("com.mysql.cj.jdbc.Driver");
 20	 Connection connection = DriverManager.getConnection(URL, USER, PASSWORD);
 21	 connection.setAutoCommit(false); // Enable manual transaction management
 22	 return connection;
 23	 } catch (ClassNotFoundException e) {
 24	 throw new SQLException("Driver MySQL tidak ditemukan: " + e.getMessage());
 25	 } catch (SQLException e) {
 26	 throw new SQLException("Gagal koneksi ke database: " + e.getMessage());
 27	 }
 28	 }
 29	
 30	 public static boolean isConnected(Connection connection) {
 31	 try {
 32	 return connection != null && !connection.isClosed();
 33	 } catch (SQLException e) {
 34	 return false;
 35	 }
 36	 }
